#' @examples
#' M <- matrix(c(1, 0.5, 0.5, 1), 2, 2)
#' draw_normal(c(0, 0), solve(M))
draw_normal <- function(mu, Sigma_inv) {
    .Call('zoofactr_draw_normal', PACKAGE = 'zoofactr', mu, Sigma_inv)
}

