#' density_normal(cbind(c(0, 0), c(2, 2)), m, solve(M), TRUE)
density_normal <- function(x, mu, Sigma_inv, logret = FALSE) {
    .Call('zoofactr_density_normal', PACKAGE = 'zoofactr', x, mu, Sigma_inv, logret)
}

